(ns advent-of-code.2022.day-06)

(def data "")

(defn find-first-marker [character-count]
  (->> (partition character-count 1 data)
       (map-indexed (fn [i col] [i (count (set col))]))
       (filter #(= character-count (last %)))
       ffirst
       (+ character-count)))

(defn part-1 [] (find-first-marker 4))
(defn part-2 [] (find-first-marker 14))