(ns advent-of-code.2022.day-06)

(def data "")

(defn part-1 []
  (->> (partition 4 1 data)
       (map-indexed (fn [i col] [i (set col) (count (set col))]))
       (filter #(= 4 (last %)))
       ffirst
       (+ 4)))

(defn part-2 []
  (->> (partition 14 1 data)
       (map-indexed (fn [i col] [i (set col) (count (set col))]))
       (filter #(= 14 (last %)))
       ffirst
       (+ 14)))